mapper.update:
UPDATE region_configs
SET is_active = #{isActive}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM region_configs
WHERE region = #{region} 
  AND language_code = #{languageCode}

    AND id != #{excludeId}

SELECT COUNT(*) > 0
FROM region_configs
WHERE region = #{region} 
  AND language_code = #{languageCode}
  AND is_active = 1
