sql.where:
deleted = 0

    AND (title LIKE CONCAT('%', #{keyword}, '%') 
         OR organizer LIKE CONCAT('%', #{keyword}, '%')
         OR location LIKE CONCAT('%', #{keyword}, '%'))


    AND type = #{type}


    AND status = #{status}


    AND date >= #{startDate}


    AND date <= #{endDate}

mapper.select:
SELECT FROM charity_activities
ORDER BY date DESC, create_time DESC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE charity_activities SET deleted = 1, update_time = NOW() WHERE id IN
#{id}
AND deleted = 0
